import React, { useEffect, useState } from "react";
import Axios from "axios";
import "../App.css";
import Select from "react-select";
import Tags from "../components/Tags";
import Cities from "../components/Cities";

export default function ProfileForm() {
  const [birthdate, setBirthdate] = useState("");
  const [gender, setGender] = useState("");
  const [orientation, setOrientation] = useState("");
  const [city, setCity] = useState("");
  const [interests, setInterests] = useState([]);
  const [bio, setBio] = useState("");
  const [status, setStatus] = useState("");
  const options = [
    { lable: "Heterosexual", value: "heterosexual" },
    { lable: "Homosexual", value: "homosexual" },
    { lable: "Bisexual", value: "bisexual" },
  ];

  Axios.defaults.withCredentials = true;

  const profileFill = () => {
    Axios.post("http://localhost:3001/completeprofile", {
      birthdate: birthdate,
      gender: gender,
      orientation: orientation,
      city: city,
      interests: interests,
      bio: bio,
    }).then((response) => {
      if (response.data.message) {
        setStatus(response.data.message);
      } else {
        setStatus(response.data[0].username);
      }
    });
  };

  return (
    <div className="App">
      <h3>{status}</h3>
      <div className="registration">
        <h1>Profile info</h1>
        <label>Birthdate</label>
        <input
          type="date"
          onChange={(e) => {
            setBirthdate(e.target.value);
          }}
        />
        <br />
        <label>Gender</label>
        <select
          onChange={(e) => {
            setGender(e.target.value);
          }}
        >
          <option value="male">Male</option>
          <option value="female">Female</option>
        </select>
        <br />
        <label>Sexual orientation</label>
        <div>
          <Select
            options={orient}
            value={orientation}
            onChange={(e) => {
              setOrientation(e.target.value);
            }}
            labelledBy="Select"
          />
        </div>
        <br />
        <label>City</label>
        <Cities
          onChange={(e) => {
            setCity(e.target.value);
          }}
        />
        <br />
        <label>Interests</label>
        <Tags
          onChange={(e) => {
            setInterests(e.target.value);
          }}
        />
        <label>Bio</label>
        <input
          type="text"
          onChange={(e) => {
            setBio(e.target.value);
          }}
        />
        <br />
        <button onClick={profileFill}> Submit </button>
        <a href="http://localhost:3000/login">Return</a>
      </div>
    </div>
  );
}
