import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";
import { useHistory } from "react-router-dom";
const Activation = () => {
  const hash = useParams().hash;
  const history = useHistory();

  console.log(hash);

  const activate = async () => {
    try {
      await axios.post(`http://localhost:5000/activate/${hash}`);
      history.push("/");
    } catch (error) {
      console.log("Error", error);
    }
    // const response = await axios.post(`http://localhost:5000/activate/${hash}`);
    // console.log(response);
  };

  return (
    <>
      <div className="activ">
        <div className=" text-center">
          <h3>Profile activated!</h3>
          <button onClick={activate} className="btn btn-primary">
            Activate
          </button>
          <Link to="/">
            <p>Go back to sign in!</p>
            <a>Login</a>
          </Link>
        </div>
      </div>
    </>
  );
};

export default Activation;
