/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect } from "react";
import axios from "axios";
import jwt_decode from "jwt-decode";
import { useHistory, useParams } from "react-router-dom";
import { EditText, EditTextarea } from "react-edit-text";
import "react-edit-text/dist/index.css";
import PicturesForm from "./PicturesForm";

const Profile = () => {
  const { id } = useParams();
  const [loggedIn, setLoggedin] = useState("");
  const [token, setToken] = useState("");
  const [expire, setExpire] = useState("");
  const [pics, setPics] = useState([]);
  const [newFirstName, setNewFirstName] = useState("");
  const [newLastName, setNewLastName] = useState("");
  const [newUsername, setNewUsername] = useState("");
  const [newBio, setNewBio] = useState("");
  const [newInterest, setNewInterest] = useState("");
  const [newGender, setNewGender] = useState("");
  const [newOrientation, setNewOrientation] = useState("");
  const [newCity, setNewCity] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const history = useHistory();

  useEffect(() => {
    refreshToken();
    getLoggedIn();
    getPicsById();
  }, []);

  const refreshToken = async () => {
    try {
      const response = await axios.get("http://localhost:5000/token");
      setToken(response.data.accessToken);
      const decoded = jwt_decode(response.data.accessToken);
      setExpire(decoded.exp);
    } catch (error) {
      if (error.response) {
        history.push("/");
      }
    }
  };

  const axiosJWT = axios.create();

  axiosJWT.interceptors.request.use(
    async (config) => {
      const currentDate = new Date();
      if (expire * 1000 < currentDate.getTime()) {
        const response = await axios.get("http://localhost:5000/token");
        config.headers.Authorization = `Bearer ${response.data.accessToken}`;
        setToken(response.data.accessToken);
        //console.log("token ",response.data.accessToken);
        const decoded = jwt_decode(response.data.accessToken);
        setExpire(decoded.exp);
      }
      return config;
    },
    (error) => {
      return Promise.reject(error);
    }
  );

  const getLoggedIn = async () => {
    const response = await axiosJWT.get("http://localhost:5000/users", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setLoggedin(response.data);
  };

  const getPicsById = async () => {
    const response = await axiosJWT.get("http://localhost:5000/user/pictures", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setPics(response.data);
  };

  const updateProfile = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`http://localhost:5000/user/update/${id}`, {
        firstName: newFirstName.value,
        lastName: newLastName.value,
        username: newUsername.value,
        email: newEmail.value,
        bio: newBio.value,
        interests: newInterest.value,
        gender: newGender.value,
        orientation: newOrientation.value,
        city: newCity.value,
      });
      history.push(`/profile/${id}`);
    } catch (error) {
      if (error.response) {
        console.log(error.response.data.msg);
      }
    }
  };

  const deletePic = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/user/pictures/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      history.push(`/profile/${id}`);
    }
    catch (error) {
      if (error.response) {
        console.log(error.response.data.msg);
      }
    }
  }

  if (pics.length > 0)
    return (
      <div className="update">
        <div className="card-pictures">
          <PicturesForm />
          <div className="uploaded-pics">
            {pics.map((pic) => (
              <img
                className="img-top"
                src={pic.pic_path}
                alt="uploaded-pic"
                key={pic.id}
                />
                ))}
                <button className="btn btn-delete" onClick={() => deletePic(pics.id)}>Delete</button>
          </div>
        </div>
        <div className="card-profile">
          <h3>✍ Update profile</h3>
          <br />
          <label>✎ Firts name</label>
          <EditText
            name="textbox1"
            defaultValue={loggedIn.firstname}
            onSave={(value) => {
              if (value !== "") {
                setNewFirstName(value);
              } else {
                setNewFirstName(loggedIn.firstname);
              }
              console.log(value);
            }}
          />
          <label>✎ Last Name</label>
          <EditText
            name="textbox1"
            defaultValue={loggedIn.lastname}
            onSave={(value) => {
              if (value !== "") {
                setNewLastName(value);
              } else {
                setNewLastName(loggedIn.lastname);
              }
              console.log(value);
            }}
          />
          <label>Age</label>
          <p>{loggedIn.birthdate}</p>
          <label>✎ Email address</label>
          <EditText
            name="textbox1"
            defaultValue={loggedIn.email}
            onSave={(value) => {
              if (value !== "") {
                setNewEmail(value);
              } else {
                setNewEmail(loggedIn.email);
              }
              console.log(value);
            }}
          />
          <div className="card-body">
            <label>✎ Username</label>
            <EditText
              name="textbox1"
              defaultValue={loggedIn.username}
              onSave={(value) => {
                if (value !== "") {
                  setNewUsername(value);
                } else {
                  setNewUsername(loggedIn.username);
                }
                console.log(value);
              }}
            />
            <label>✎ Gender</label>
            <EditText
              name="textbox1"
              defaultValue={loggedIn.gender}
              onSave={(value) => {
                if (value !== "") {
                  setNewGender(value);
                } else {
                  setNewGender(loggedIn.gender);
                }
                console.log(value);
              }}
            />
            <label>✎ Sexual orientation</label>
            <EditText
              name="textbox1"
              defaultValue={loggedIn.orientation}
              onSave={(value) => {
                if (value !== "") {
                  setNewOrientation(value);
                } else {
                  setNewOrientation(loggedIn.orientation);
                }
                console.log(value);
              }}
            />

            <label>✎ Location</label>
            <EditText
              name="textbox1"
              defaultValue={loggedIn.city}
              onSave={(value) => {
                if (value !== "") {
                  setNewCity(value);
                } else {
                  setNewCity(loggedIn.city);
                }
                console.log(value);
              }}
            />
            <label>✎ Interests</label>
            <EditText
              name="textbox1"
              defaultValue={loggedIn.interests}
              onSave={(value) => {
                if (value !== "") {
                  setNewInterest(value);
                } else {
                  setNewInterest(loggedIn.interests);
                }
                console.log(value);
              }}
            />
            <label>✎ Bio</label>
            <EditTextarea
              name="textbox1"
              defaultValue={loggedIn.bio}
              onSave={(value) => {
                if (value !== "") {
                  setNewBio(value);
                } else {
                  setNewBio(loggedIn.bio);
                }
                console.log(value);
              }}
            />
          </div>
          <button className="btn btn-warning" onClick={updateProfile}>
            Update
          </button>
        </div>
      </div>
    );
  else return <div>Loading...</div>;
};

export default Profile;
