import React, { useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";
import Gender from "../models/Gender";
import Tags from "../models/Tags";
import Cities from "../models/Cities";
import Orientation from "../models/Orientation";
import "../App.css";

const ProfileForm = () => {
  const [birthdate, setBirthdate] = useState("");
  const [gender, setGender] = useState("");
  const [orientation, setOrientation] = useState("");
  const [city, setCity] = useState("");
  const [interests, setInterests] = useState([]);
  const [bio, setBio] = useState("");
  const [message, setMessage] = useState("");
  const history = useHistory();

  const [image, setImage] = useState({ preview: "", data: "" });
  const [status, setStatus] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    let formData = new FormData();
    formData.append("file", image.data);
    const response = await fetch("http://localhost:5000/image", {
      method: "POST",
      body: formData,
    });
    if (response) setStatus(response.statusText);
  };

  const handleFileChange = (e) => {
    const img = {
      preview: URL.createObjectURL(e.target.files[0]),
      data: e.target.files[0],
    };
    setImage(img);
  };

  const profileFill = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/fill", {
        birthdate: birthdate,
        gender: gender,
        orientation: orientation,
        city: city,
        interests: interests,
        bio: bio,
        profile_pic: im.data
      });
      history.push("/dashboard");
    } catch (error) {
      if (error.response) {
        setMessage(error.response.data.msg);
      }
    }
  };
  const onSaveMybus = () => {
    profileFill();
    handleSubmit();
  };
  return (
    <div>
      <div className="Auth-form-container">
        <form className="Auth-form" onSubmit={onSaveMybus}>
          <div className="Auth-form-content">
            <h3 className="Auth-form-title">Complete profile</h3>
            <div className="form-group mt-3">
              <label>Birthdate</label>
              <div className="form-group mt-3">
                <input
                  type="date"
                  onChange={(e) => {
                    setBirthdate(e.target.value);
                  }}
                />
              </div>
              <br />
              <label>Gender</label>
              <Gender setGender={setGender} />
              <br />
              <label>Sexual orientation</label>
              <Orientation setOrientation={setOrientation} />
              <br />
              <label>City</label>
              <Cities setCity={setCity} />
              <br />
              <label>Interests</label>
              <div className="form-group mt-3">
                <Tags setInterests={setInterests} />
              </div>
              <br />
              <label>Bio</label>
              <div className="form-group mt-3">
                <input
                  className="form-control mt-1"
                  type="text"
                  onChange={(e) => {
                    setBio(e.target.value);
                  }}
                />
              </div>
              <br />
              <div className="App">
                <label>Choose profile picture</label>
                <br />
                {image.preview && (
                  <img src={image.preview} width="100" height="100" />
                )}
                <hr></hr>
                <input
                  className="form-control"
                  type="file"
                  name="file"
                  onChange={handleFileChange}
                ></input>
                {status && <h4>{status}</h4>}
              </div>
              <div className="d-grid gap-2 mt-3">
                <button className="btn btn-outline-warning">Submit</button>
                <a href="http://localhost:3000/dashboard"> Skip</a>
              </div>
            </div>
            <p className="message">{message}</p>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProfileForm;
