/* eslint-disable */
import React from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";
import logo from "../assets/logo.png";
import chat from "../assets/chat.png";
import notification from "../assets/notification.png";
import logout from "../assets/logout.png";
import user from "../assets/user.png";
import { useEffect } from "react";
import { useState } from "react";
import { useCookies } from "react-cookie";

const Navbar = ({ socket }) => {
  const [cookie, setCookie] = useCookies(["refreshToken"]);
  const [loggedIn, setLoggedin] = useState("");
  const [notifications, setNotifications] = useState([]);
  const [messages, setMessages] = useState([]);
  const [open, setOpen] = useState(false);
  const history = useHistory();

  useEffect(() => {
    if (socket) {
      socket.on("getNotification", (data) => {
        setNotifications((prev) => [...prev, data]);
      });
      socket.on("getText", (data) => {
        setMessages((prev) => [...prev, data]);
      });
    }
    const getLoggedIn = async () => {
      const response = await axios.get(
        `http://localhost:5000/user/${cookie.refreshToken}`,
        {}
      );
      setLoggedin(response.data);
    };
    getLoggedIn();
  }, [socket]);

  // const dispayMessage = ({ senderName, text }) => {
  //   return (
  //     <div className="message">
  //       <p>{senderName}</p>
  //       <p>{text}</p>
  //     </div>
  //   );
  // };

  const displayNotifications = ({ senderName, type }) => {
    let action;
    if (type === "like") {
      action = "liked you";
    } else if (type === "dislike") {
      action = "unliked you";
    } else if (type === "profile view") {
      action = "viewed your profile";
    }
    return (
      <span
        className="notification"
        key={type}
      >{`${senderName} ${action}`}</span>
    );
  };

  const handleRead = () => {
    setNotifications([]);
    setOpen(false);
  };

  const Chat = () => {
    history.push(`/chat/${loggedIn.id}`);
  };

  const MyProfile = () => {
    history.push(`/profile/${loggedIn.id}`);
  };

  const Logout = async () => {
    try {
      await axios.delete("http://localhost:5000/logout");
      history.push("/");
    } catch (error) {
      console.log(error);
    }
  };
  const Dashboard = () => {
    history.push("/dashboard");
  };
  //console.log("socket", socket);

  //console.log("notiffff", notifications);

  return (
    <>
    <nav class="navbar navbar-expand-lg navbar-light bg-light">
  <div class="container-fluid">
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarTogglerDemo01" aria-controls="navbarTogglerDemo01" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarTogglerDemo01">
      <a class="navbar-brand" href="#">Hidden brand</a>
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item">
          <a class="nav-link active" aria-current="page" href="#">Home</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#">Link</a>
        </li>
        <li className="nav-item">
          <a className="nav-link disabled" href="#" tabindex="-1" aria-disabled="true">Disabled</a>
        </li>
      </ul>
      <form className="d-flex">
        <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
        <button className="btn btn-outline-success" type="submit">Search</button>
      </form>
    </div>
  </div>
</nav>
      <nav
        className="navbar is-light"
        role="navigation"
        aria-label="main navigation"
      >
        <div className="container">
          <div className="navbar-brand">
            <div className="Nav-logo">
              <img onClick={Dashboard} src={logo} alt="logo" />
            </div>
          </div>

          <div id="navbarBasicExample" className="navbar-menu">
            <div className="navbar-end">
              <div className="navbar-item">
                <div className="buttons">
                  <div className="icon" onClick={MyProfile}>
                    <img src={user} className="iconImg" alt="profile" />
                  </div>
                  <div className="icon" onClick={() => setOpen(!open)}>
                    <img src={notification} className="iconImg" alt="notif" />
                    {notifications.length > 0 && (
                      <div className="counter"></div>
                    )}
                  </div>
                  <div className="icon">
                    <img
                      onClick={Chat}
                      src={chat}
                      alt="chat"
                      className="iconImg"
                    />
                    {messages.length > 0 && <div className="counter"></div>}
                  </div>
                  <div className="icon">
                    <img
                      onClick={Logout}
                      src={logout}
                      alt="logout"
                      className="iconImg"
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </nav>
      {open && (
        <div className="notifications">
          {notifications.map((n) => displayNotifications(n))}
          <button className="btn btn-warning" onClick={handleRead}>
            Mark as read
          </button>
        </div>
      )}
    </>
  );
};

export default Navbar;
