import { BrowserRouter, Route, Switch, Redirect } from "react-router-dom";
import { UserContextProvider } from "./context/UserContext";
import Dashboard from "./components/Dashboard";
import Login from "./components/Login";
import Navbar from "./components/Navbar";
import Register from "./components/Register";
import ProfileForm from "./components/ProfileForm";
import PicturesForm from "./components/PicturesForm";
import ForgotPasswords from "./components/ForgotPassword";
import Profile from "./components/Profile";
import { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";
import socketIOClient from 'socket.io-client'
//import jwt_decode from "jwt-decode";
const ENDPOINT = 'http://localhost:3001'
function App() {
  const [token, setToken] = useState("");
  const [loggedIn, setLoggedin] = useState("");
  const history = useHistory();
  const socket = socketIOClient(ENDPOINT)

  useEffect(() => {
    refreshToken();
    getLoggedIn();
  }, []);

  const refreshToken = async () => {
    try {
      const response = await axios.get("http://localhost:5000/token");
      setToken(response.data.accessToken);
      //const decoded = jwt_decode(response.data.accessToken);
      socket.emit("setUserData");
    } catch (error) {
      if (error.response) {
        history.push("/");
      }
    }
  };
  const axiosJWT = axios.create();

  const getLoggedIn = async () => {
    try {
      const response = await axiosJWT.get("http://localhost:5000/users", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setLoggedin(response.data);
    } catch (error) {
      if (error.response) {
        history.push("/");
      }
    }
  };

  const NavRoute = ({exact, path, component: Component}) => (
    <Route exact={exact} path={path} render={(props) => (
        <div>
            <Navbar socket={socket} />
            <Component {...props}/>
        </div>
    )}/>
)

  if (!loggedIn)
    return (
      <UserContextProvider>
        <BrowserRouter>
          <Switch>
            <Route exact path="/">
              <Login />
            </Route>
            <Route path="/forgotpassword">
              <ForgotPasswords />
            </Route>
            <Route path="/register">
              <Register />
            </Route>
          </Switch>
        </BrowserRouter>
      </UserContextProvider>
    );
  return (
    <>
    <UserContextProvider>
      <BrowserRouter>
        <Switch>
          <Route path="/completeprofile">
            <Navbar />
            <ProfileForm />
          </Route>
          <Route path="/pictures">
            <Navbar />
            <PicturesForm />
          </Route>
          <Route path="/dashboard">
            <Navbar />
            <Dashboard />
          </Route>
          <Route path="/profile">
            <Navbar />
            <Profile />
          </Route>
          <Route path="/">
                {loggedIn.birthdate !== 0 ? <Redirect to="/profilepage" /> : <Redirect to="/completeprofile"/> }
            </Route>

          {/* {loggedIn.birthdate === 0 ? (
          <Redirect to="/completeprofile" />
        ) : (
          <Redirect to="/dashboard" />
        )} */}
        </Switch>
      </BrowserRouter>
    </UserContextProvider>
    </>
  );
}

export default App;
