/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect } from "react";
import axios from "axios";
import jwt_decode from "jwt-decode";
import { useHistory } from "react-router-dom";

const Dashboard = () => {
  const [loggedIn, setLoggedin] = useState("");
  const [token, setToken] = useState("");
  const [expire, setExpire] = useState("");
  const [users, setUsers] = useState([]);
  const [pics, setPics] = useState([]);
  const history = useHistory();

  useEffect(() => {
    refreshToken();
    getUsers();
    getLoggedIn();
    getPicsById();
  }, []);

  const refreshToken = async () => {
    try {
      const response = await axios.get("http://localhost:5000/token");
      setToken(response.data.accessToken);
      const decoded = jwt_decode(response.data.accessToken);
      setExpire(decoded.exp);
    } catch (error) {
      if (error.response) {
        history.push("/");
      }
    }
  };

  const axiosJWT = axios.create();

  axiosJWT.interceptors.request.use(
    async (config) => {
      const currentDate = new Date();
      if (expire * 1000 < currentDate.getTime()) {
        const response = await axios.get("http://localhost:5000/token");
        config.headers.Authorization = `Bearer ${response.data.accessToken}`;
        setToken(response.data.accessToken);
        //console.log("token ",response.data.accessToken);
        const decoded = jwt_decode(response.data.accessToken);
        setExpire(decoded.exp);
      }
      return config;
    },
    (error) => {
      return Promise.reject(error);
    }
  );

  const getUsers = async () => {
    const response = await axiosJWT.get("http://localhost:5000/users/info", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setUsers(response.data);
  };
  const getLoggedIn = async () => {
    const response = await axiosJWT.get("http://localhost:5000/users", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setLoggedin(response.data);
  };

  const getPicsById = async () => {
    const response = await axiosJWT.get("http://localhost:5000/user/pictures", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setPics(response.data);
  };

  const handleUserSelect = async (id) => {
    history.push(`/users/${id}`);
  };

  return (
    <div className="container mt-5">
      <h1>Logged in as: {loggedIn.username}</h1>
      <table className="table is-striped is-fullwidth">
        <thead>
          <tr>
            <th>Pic</th>
            <th>Name</th>
            <th>Email</th>
            <th>Birthdate</th>
            <th>Gender</th>
            <th>Sexual orientation</th>
            <th>City</th>
            <th>Online</th>
          </tr>
        </thead>

        <tbody>
          {users &&
            users.map((user) => {
              return (
                <tr onClick={() => handleUserSelect(user.id)} key={user.id}>
                  <td>
                    <img src={user.profile_pic} width="300" height="250" />{" "}
                  </td>
                  <td>{user.username}</td>
                  <td>{user.email}</td>
                  <td>{user.birthdate}</td>
                  <td>{user.gender}</td>
                  <td>{user.orientation}</td>
                  <td>{user.city}</td>
                  <td>{user.online}</td>
                </tr>
              );
            })}
        </tbody>
        <div className="text-center">
        <h2 className="text-center">
          {loggedIn.firstname} {loggedIn.lastname}
        </h2>
        <StarRating rating={5} />
        <div className="card">
        {users &&
            users.map((user) => {
              return (
                <>
          <img  onClick={() => handleUserSelect(user.id)} key={user.id} className="card-img-top" src={user.profile_pic} alt="Card cap" />
          <div className="card-body">
            <h5 className="card-title">{user.username}</h5>
            <label>Age</label>
            <p className="card-text">{user.birthdate}</p>
            <label>Bio</label>
            <p className="card-text">{user.bio}</p>
            <label>Gender</label>
            <p className="card-text">{user.gender}</p>
            <label>Location</label>
            <p className="card-text">{user.city}</p>
            <label>Interests</label>
            <p className="card-text">{user.interests}</p>
          </div>
          </>
            );
          })}
        </div>
      </div>

      </table>
    </div>
  );
};

export default Dashboard;
