/* eslint-disable */
import { BrowserRouter, Route, Switch } from "react-router-dom";
import { UserContextProvider } from "./context/UserContext";
import Dashboard from "./components/Dashboard";
import Login from "./components/Login";
import Navbar from "./components/Navbar";
import Register from "./components/Register";
import ProfileForm from "./components/ProfileForm";
import PicturesForm from "./components/PicturesForm";
import ForgotPasswords from "./components/ForgotPassword";
import Profile from "./components/Profile";
import ProfileRandom from "./components/ProfileRandom";
import Footer from "./models/Footer";
import ResetPassword from "./components/ResetPassword";
import Activation from "./components/Activation";
import { useCookies } from "react-cookie";
import { useEffect, useState } from "react";
// import { useHistory } from "react-router-dom";
import axios from "axios";
// import socketIOClient from "socket.io-client";
import { io } from "socket.io-client";
//import jwt_decode from "jwt-decode";
// const ENDPOINT = "http://localhost:5000";
// const socket = socketIOClient(ENDPOINT);

function App() {
  const [cookie, setCookie] = useCookies(["refreshToken"]);
  const [user, setUser] = useState("");
  const [socket, setSocket] = useState(null);

  console.log("cookie", cookie.refreshToken);

  useEffect(() => {
    setSocket(io("http://localhost:5000"));
    if (cookie.refreshToken) {
      const getLoggedIn = async () => {
        const response = await axios.get(
          `http://localhost:5000/user/${cookie.refreshToken}`,
          {}
        );
        setUser(response.data.id);
      };
      getLoggedIn();
    }
  }, []);

  useEffect(() => {
    if (user) {
      socket.emit("addOnlineUser", user);
    }
  }, [user]);

  console.log("user", user);

  return (
    <>
      <UserContextProvider>
        <BrowserRouter>
          <Switch>
            <Route exact path="/">
              <Login />
            </Route>
            <Route path="/forgotpassword">
              <ForgotPasswords />
            </Route>
            <Route path="/resetpassword/:token">
              <ResetPassword />
            </Route>
            <Route path="/register">
              <Register />
            </Route>
            <Route path="/activate/:hash">
              <Activation />
            </Route>
            <Route path="/completeprofile">
              <Navbar socket={socket} />
              <ProfileForm />
            </Route>
            <Route path="/pictures">
              <Navbar socket={socket} />
              <PicturesForm />
            </Route>
            <Route path="/dashboard">
              <Navbar socket={socket} />
              <Dashboard user= {user} socket={socket} />
            </Route>
            <Route path="/profile/:id">
              <Navbar socket={socket} />
              <Profile />
            </Route>
            <Route path="/users/:id">
              <Navbar socket={socket} />
              <ProfileRandom />
            </Route>
            {/* <Route path="/">
              {loggedIn.birthdate !== 0 ? (
                <Redirect to="/profile" />
              ) : (
                <Redirect to="/completeprofile" />
              )}
            </Route> */}
          </Switch>
          <Footer />
        </BrowserRouter>
      </UserContextProvider>
    </>
  );
}

export default App;

